rcent = 0;						break;				}				$check_packet_active = $this -> model_pd_registercustom -> get_ml_child_active($customer['customer_id'],$max_pd_customer['filled']);								if (intval($check_packet_active) >= 3)				{					$amount_receive = $customer['amount'] * $percent / $customer['percent'];				}				else				{					$amount_receive =  $customer['amount'] * ($percent - 10) / $customer['percent'];				}								// bk r_wallet				$this -> model_pd_registercustom -> insert_hh_wallet_bk($customer['customer_id'],$amount_receive);				//update ví r = 0				$this -> model_pd_registercustom ->update_hh_wallet0($customer['customer_id']);				$balance_hh = $this -> model_pd_registercustom -> Get_amount_HH_Wallet($customer['customer_id']);				// lưu lịch sử								$id_history = $this -> model_pd_registercustom -> saveTranstionHistory(	            $customer['customer_id'],	            'Trả hoa hồng trên thu nhập trực tiếp của F1', 	            '- ' . ($amount_receive/1000) . ' ĐT',	            "Rút hoa hồng trên thu nhập trực tiếp của F1 (". (number_format($amount_receive*27)) ." VNĐ)",	            $balance_hh	            );				$i++;				//print_r($customer); die;				$objPHPExcel->getActiveSheet()->setCellValue('A'.$n,$i);				$objPHPExcel->getActiveSheet()->setCellValue('B'.$n," ".$customer['username']);				$objPHPExcel->getActiveSheet()->setCellValue('C'.$n,$customer['account_holder']);				$objPHPExcel->getActiveSheet()->setCellValue('D'.$n," ".$customer['account_number']);				$objPHPExcel->getActiveSheet()->setCellValue('E'.$n," ".($amount_receive/1000));				$objPHPExcel->getActiveSheet()->setCellValue('F'.$n," ".(number_format($amount_receive*27)));				$objPHPExcel->getActiveSheet()->setCellValue('G'.$n,$customer['bank_name']);				$objPHPExcel->getActiveSheet()->setCellValue('H'.$n,$customer['branch_bank']);				$objPHPExcel->getActiveSheet()->setCellValue('I'.$n," ".$customer['telephone']);				$n++;				$tongtien += doubleval($amount_receive*2